DROP TABLE IF EXISTS `table_booktype`;
CREATE TABLE `table_booktype` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `bookTypeName` varchar(20) DEFAULT NULL,
  `bookTypeDesc` varchar(1000) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of table_booktype
-- ----------------------------
INSERT INTO `table_booktype` VALUES ('1', '小说', '小说是以刻画人物形象为中心，通过完整的故事情节和环境描写来反映社会生活的文学体裁。');
INSERT INTO `table_booktype` VALUES ('2', '计算机科学', '计算机科学，研究计算机及其周围各种现象和规律的科学，亦即研究计算机系统结构、程序系统（即软件）、人工智能以及计算本身的性质和问题的学科。');
INSERT INTO `table_booktype` VALUES ('3', '经济学', '经济学（Economics），是研究人类经济活动的规律即价值的创造、转化、实现的规律——经济发展规律的理论，分为政治经济学与科学经济学两大类型。');
INSERT INTO `table_booktype` VALUES ('4', '管理学', '管理学 研究管理规律、探讨管理方法、建构管理模式、取得最大管理效益的学科。');
INSERT INTO `table_booktype` VALUES ('5', '哲学', '哲学(英文：Philosophy)是对基本和普遍之问题的研究的学科，是关于世界观的理论体系。');
INSERT INTO `table_booktype` VALUES ('7', '历史', '历史，简称史，一般指人类社会历史，它是记载和解释一系列人类活动进程的历史事件的一门学科，多数时候也是对当下时代的映射。');
INSERT INTO `table_booktype` VALUES ('8', '心理学', '心理学是一门研究人类心理现象及其影响下的精神功能和行为活动的科学，兼顾突出的理论性和应用（实践）性。');
INSERT INTO `table_booktype` VALUES ('9', '建筑学', '建筑学，从广义上来说，是研究建筑及其环境的学科。建筑学是一门横跨工程技术和人文艺术的学科。');
INSERT INTO `table_booktype` VALUES ('11', '医学', '医学，是通过科学或技术的手段处理生命的各种疾病或病变的一种学科，促进病患恢复健康的一种专业。');

